import {CellContext, ColumnDef, createColumnHelper } from "@tanstack/react-table";
import {ITask, TaskDescription, TaskName, UpdateTaskProps} from "@entities/task";
import {ITaskStatus} from "@entities/status";
import {TaskTrait, TaskTraitAdd} from "@entities/trait";
import React from "react";
import {ITaskTag} from "@entities/tag";
import {QueryClient, UseMutationResult, useQueryClient, UseQueryResult} from "@tanstack/react-query";
import {IPaginatorResponse} from "@shared/api";
import {TaskTableCell, TaskTableCellContent} from "@shared/ui";
import {TaskDeadline} from "@entities/task/ui/TaskDeadline";
import {getInputDateFromDeadline, getTimestampFromISODate} from "@shared/lib";

export function useTaskTableColumns<Task extends ITask, TaskUpdate extends ITask<number, number>>(props: {
    extendedColumns: ColumnDef<Task,  any>[];
    extendAfter: number;

    apiGetSubtasks: (task_id: number) => UseQueryResult<IPaginatorResponse<Task>>;
    apiUpdateTask: (queryClient: QueryClient) => UseMutationResult<Task, Error, UpdateTaskProps<TaskUpdate>>;
    apiCreateSubtask: (queryClient: QueryClient) => UseMutationResult<Task, Error, UpdateTaskProps<TaskUpdate>>;
}): ColumnDef<Task, any>[] {
    const {
        extendedColumns, extendAfter,
        apiGetSubtasks, apiUpdateTask, apiCreateSubtask,
    } = props;

    const queryClient = useQueryClient();

    const {
        mutate: updateMutation,
        isError: isUpdateError,
        error: updateError
    } = apiUpdateTask(queryClient);

    const columnHelper = createColumnHelper<Task>();
    const columns: ColumnDef<Task,  any>[] = [
        columnHelper.accessor((task) => task, {
            header: "Название",
            cell: (context: CellContext<Task, Task>) => (
                <TaskName accessKey={"name"}
                    onExpand={(e) => {

                    }}
                    onAddSubtask={(e) => {
                        console.log("create subtask");
                    }}
                    onChange={(e) => {
                        updateMutation({
                            taskId: context.getValue().id,
                            task: {
                                name: e.target.innerText
                            } as Partial<TaskUpdate>
                        });
                    }}
                    name={context.getValue().name}
                />
            )
        }),
        columnHelper.accessor(({status}) => status, {
            header: "Статус",
            cell: (context: CellContext<Task, ITaskStatus>) => (
                <TaskTableCellContent hCenter vCenter>
                    {
                        context.getValue() ? <TaskTrait trait={context.getValue()} /> :
                            <TaskTraitAdd<ITaskStatus> />

                    }
                </TaskTableCellContent>
            )
        }),
        columnHelper.accessor((task) => task, {
            header: "Теги",
            cell: (context: CellContext<Task, ITask>) => (
                <TaskTableCellContent vCenter>
                    {
                        context.getValue().tags?.map(
                            tag => (
                                <TaskTrait
                                    removable
                                    key={tag.id}
                                    trait={tag}
                                    onRemove={(tagId) => {
                                        updateMutation({
                                            taskId: context.getValue().id,
                                            task: {
                                                tags: context.getValue().tags.filter(tag => tag.id !== tagId)
                                                    .map(tag => tag.id)
                                            } as Partial<TaskUpdate>
                                        });
                                    }}
                                />
                            )
                        )
                    }
                    <TaskTraitAdd<ITaskTag> />
                </TaskTableCellContent>
            )
        }),
        columnHelper.accessor((task) => task, {
            header: "Дедлайн",
            cell: (context: CellContext<Task, Task>) => (
                <TaskTableCellContent hCenter vCenter>
                    <TaskDeadline
                        deadline={context.getValue().deadline ? getInputDateFromDeadline(context.getValue().deadline) : undefined}
                        onChange={(e) => {
                            updateMutation({
                                taskId: context.getValue().id,
                                task: {
                                    deadline: e.target.valueAsNumber
                                } as Partial<TaskUpdate>
                            });
                        }}
                    />
                </TaskTableCellContent>
            )
        }),
        columnHelper.accessor((task) => task, {
            header: "Описание",
            cell: (context: CellContext<Task, Task>) => (
                <TaskDescription
                    onChange={(e) => {
                        updateMutation({
                            taskId: context.getValue().id,
                            task: {
                                description: e.target.innerText
                            } as Partial<TaskUpdate>
                        });
                    }}
                    description={context.getValue().description}
                />
            )
        }),
    ];

    switch(extendAfter) {
        case -1: {
            return [
                ...columns,
                ...extendedColumns
            ]
        }
        case 0: {
            return [
                ...extendedColumns,
                ...columns
            ]
        }
        default: {
            return [
                ...columns.slice(0, extendAfter),
                ...extendedColumns,
                ...columns.slice(extendAfter + 1)
            ];
        }
    }
}
