import React from 'react';
import {IPersonalTask, PersonalTaskRepository} from "@entities/task";
import {ITaskStatus} from "@entities/status";
import {ITaskTag} from "@entities/tag";
import cn from "classnames";

import s from "./styles.module.scss";
import {useTaskTable} from "@widgets/task_table/hooks";
import {LoaderHandler} from "@shared/ui";

export const TaskTable = () => {
    const {tasks} = useTaskTable<IPersonalTask<ITaskStatus, ITaskTag>>({
        getCallback: () => {
            const {isLoading, isError, data} = PersonalTaskRepository.getList();
            return {isLoading, isError, data};
        }
    });

    return (
        <div className={s.taskTable}>
            <div className={cn(s.taskTableRow, s.taskTableRow_header)}>

            </div>

            <LoaderHandler isLoaded={!!tasks}>
                {
                    tasks?.map(task => (
                        <p key={task.id}>{task.name}</p>
                    ))
                }
            </LoaderHandler>
        </div>
    );
};
