import React, {ChangeEvent, FC, InputHTMLAttributes, MouseEvent, PropsWithChildren, useRef} from 'react';
import {ASSETS_PATH} from "@shared/config";
import cn from "classnames";

import s from "./styles/TaskName.module.scss";

type TaskNameProps = PropsWithChildren<{
    name: string;

    onExpand: (e: MouseEvent<HTMLImageElement>) => void;
    onAddSubtask: (e: MouseEvent<HTMLImageElement>) => void;
    onChange: (e: ChangeEvent<HTMLElement>) => void;
}> & InputHTMLAttributes<HTMLInputElement>;

export const TaskName: FC<TaskNameProps> = (props) => {
    const {name, onExpand, onAddSubtask, onChange} = props;

    const input = useRef<HTMLDivElement>(null);

    return (
        <div className={s.taskName}>
            <img
                className={cn(s.taskName__button)}
                src={ASSETS_PATH.EXPAND}
                title={"Раскрыть"}
                alt={""}
                onClick={onExpand}
            />
            <img
                src={ASSETS_PATH.PLUS}
                title={"Добавить подзадачу"}
                alt={""}
                onClick={onAddSubtask}
            />
            <div
                contentEditable={true}
                suppressContentEditableWarning={true}
                ref={input}
                onBlur={onChange}
                onKeyDown={(e) => {
                    if(e.key === "Enter") {
                        e.preventDefault();
                        e.stopPropagation();
                        input.current?.blur();
                    }
                }}
            >{name}</div>
            <img
                src={ASSETS_PATH.EDIT}
                title={"Изменить"}
                alt={""}
            />
        </div>
    );
};
