import React, {useState} from 'react';
import {
    Form,
    FormColorPicker,
    FormImage,
    FormInput,
    FormLabel,
    FormReference,
    FormSubmit,
    LoaderHandler
} from "@shared/ui";
import {userStore} from "@entities/user";
import {ROUTES} from "@shared/config";
import {useForm, useFormTextField} from "@shared/hooks";
import {CommonValidator} from "@shared/lib";
import {useFormImageField} from "@shared/hooks/form/use-form-image-field";
import {Navigate, useParams} from 'react-router-dom';
import {ImageField, TextField} from "@shared/types/form";
import {SpaceRepository} from "@entities/space";
import {useQueryClient} from "@tanstack/react-query";

const validators = [
    CommonValidator.required(),
    CommonValidator.specialChars(),
];

export const SpaceSettingsForm = () => {
    const spaceParam = useParams().spaceId;
    const spaceId = spaceParam ? parseInt(spaceParam) : null;

    const queryClient = useQueryClient();
    const {
        mutateAsync
    } = SpaceRepository.update(spaceId!, queryClient);

    const {
        data: space,
        isSuccess: isSpace
    } = SpaceRepository.getById(spaceId!);

    const name = useFormTextField("name", validators);
    const avatar = useFormImageField("avatar", []);

    const form = useForm<TextField, void, string>({
        fields: [name],
        apiCall: async () => {
            await mutateAsync
        },
        defaultError: "",
        defaultSuccessMessage: "Пространство успешно изменено!"
    });

    if(!spaceId) {
        return <Navigate to={ROUTES.USER.SPACE.ROOT()} replace />
    }

    return (
        <LoaderHandler isLoaded={isSpace}>
            <Form onSubmit={form.handleFormSubmit} center wide>
                <FormInput type={"text"} placeholder={"Введите имя пространства..."} mask={""}
                           header={"Название"}
                           onChange={name.handleChange}
                           error={name.error}
                           defaultValue={name.value}
                />

                <FormImage
                    header={"Загрузите аватар"}
                    src={avatar.value || space!.avatar}
                    onChange={avatar.handleChange}
                />

                {
                    !!form.successMessage.length && (
                        <FormLabel success>{form.successMessage}</FormLabel>
                    )
                }

                <FormReference to={ROUTES.USER.SPACE.TASK()}>Перейти к задачам</FormReference>

                <FormSubmit>Изменить</FormSubmit>
                <FormReference to={ROUTES.USER.SETTINGS.CHANGE_PASSWORD()}>Вернуться к списку</FormReference>
            </Form>
        </LoaderHandler>
    );
};
