import React, {useEffect} from 'react';
import {useTaskTable} from "@shared/hooks";
import {IPersonalTask, PERSONAL_TASK_QUERIES, PersonalTaskRepository} from "@entities/task";
import {LoaderHandler, TaskTable} from "@shared/ui";
import { QueryClient } from '@tanstack/react-query';

export const PersonalTaskTable = () => {
    const {
        headers, rows,
        tasks, updateTable
    } = useTaskTable<IPersonalTask, IPersonalTask<number, number>>({
        queryKey: PERSONAL_TASK_QUERIES.TASKS,
        apiGet: (params?: string) => PersonalTaskRepository.getList(params),
        apiGetSubtasks: (task_id: number) => PersonalTaskRepository.getSubtasks(task_id),
        apiUpdateTask: (queryClient: QueryClient) => PersonalTaskRepository.updateTask(queryClient),
        apiCreateSubtask: (queryClient: QueryClient) => PersonalTaskRepository.createSubtask(queryClient),
    });

    useEffect(() => {
        updateTable();
    }, [updateTable]);

    return (
        <LoaderHandler isLoaded={!!tasks}>
            <TaskTable headers={headers} rows={rows} />
        </LoaderHandler>
    );

