import {ITask} from "@entities/task";
import {DefaultTaskFilter} from "@shared/types";
import {useState} from "react";

export function useTaskFilter<Task extends ITask>(props: {
    filters: DefaultTaskFilter,
}): {
    url: string;
} {
    const {filters} = props;

    const url = [
        `search=${filters.name || ""}`,
        filters.status.map(status => `status=${status || ""}`).join("&"),
        filters.tags.map(tag => `tags=${tag || ""}`).join("&"),
    ].join("&");

    return {
        url
    };
}
