import React, {ChangeEvent, useState} from 'react';
import {
    IRecoveryValidatorErrors,
    IRecoveryValidatorParams,
    RecoveryValidator,
    Validator
} from "@shared/lib";
import {Form, FormInput, FormReference, FormSubmit} from "@shared/ui";
import {ROUTER_URL} from "@shared/config";

export const RecoveryForm = () => {
    const [params, setParams] = useState<IRecoveryValidatorParams>({
        email: ""
    });
    const [errorHandler, setErrorHandler] = useState<IRecoveryValidatorErrors>({});

    const onSubmit = async () => {
        const errors = RecoveryValidator.validate(params);

        setErrorHandler(errors);

        // if(Validator.hasError(errors)) {
        //     return;
        // }

        try {

        }
        catch {

        }

    }

    const onChange = (e: ChangeEvent<HTMLInputElement>, key: keyof IRecoveryValidatorParams) => {
        if(errorHandler[key]) {
            setErrorHandler({...errorHandler, [key]: undefined});
        }
        const value = e.target.value;
        setParams({...params, [key]: value});
    }

    return (
        <Form onSubmit={onSubmit} center>
            <FormInput type={"text"} placeholder={"Введите почту..."} mask={""}
                       header={"Электронная почта"}
                       onChange={(e) => onChange(e, "email")}
                       error={errorHandler.email}
            />

            <FormSubmit>Восстановить</FormSubmit>
            <FormReference to={ROUTER_URL.LOGIN}>Вернуться к авторизации</FormReference>
        </Form>
    );
};
