import {useCallback} from "react";
import {useQueryClient, UseQueryResult} from "@tanstack/react-query";
import {IPaginatorResponse} from "@shared/api";
import {Column, TableInstance, useTable} from "react-table";

export function useTaskTable<Task extends object>(props: {
    tableColumns: Column<Task>[];
    queryKey: string;
    apiGet: () => UseQueryResult<IPaginatorResponse<Task>>;
}): {
    table: TableInstance<Task>;
    tasks: Task[] | null;
    updateTable: () => void;
} {
    const {tableColumns, queryKey, apiGet} = props;

    const queryClient = useQueryClient();

    const {
        data: response,
    } = apiGet();
    const tasks = response?.results || null;

    const updateTable = useCallback(async () => {
        await queryClient.resetQueries({
            queryKey: [queryKey]
        });
    }, []);

    const table = useTable({columns: tableColumns, data: []});

    return {
        table,
        tasks,
        updateTable
    }
}
