import React from 'react';
import {Form, FormInput, FormReference, FormSubmit} from "@shared/ui";
import {ROUTER_URL} from "@shared/config";

import s from "./styles.module.scss";

export const LoginForm = () => {
    return (
        <Form className={s.loginForm}>
            <FormInput type={"text"} placeholder={"Введите логин..."} mask={""}
                       header={"Логин"}
            />
            <FormInput type={"password"} placeholder={"Введите пароль..."} mask={""}
                       header={"Пароль"}
            />

            <FormReference to={ROUTER_URL.RECOVERY}>Забыли пароль?</FormReference>

            <FormSubmit>Войти</FormSubmit>
            <FormReference to={ROUTER_URL.REGISTRATION}>Перейти к регистрации</FormReference>
        </Form>
    );
};
