import {IUser, UserUpdateDto} from "@entities/user";
import {apiInstance, IPaginatorResponse} from "@shared/api";

const BASE_URL = "users/";

const ENDPOINTS = {
    ROOT: `${BASE_URL}`,
    ME: `${BASE_URL}me/`,
}

export class UserService {
    static async getMe(): Promise<IUser> {
        return await apiInstance.get(ENDPOINTS.ME);
    }

    static async getAll(): Promise<IPaginatorResponse<IUser>> {
        return await apiInstance.get(ENDPOINTS.ROOT);
    }

    static async update(userId: number, profile: UserUpdateDto): Promise<IUser> {
        return await apiInstance.patch(ENDPOINTS.ROOT, profile)
    }
}
