export interface IValidatorData<T> {
    [index: string]: T;
}

export class Validator {
    static validate(data: IValidatorData<string[]>) {
        const errors: IValidatorData<string> = {};

        for(const key in data) {
            const value = data[key];
            if(value) {
                errors[key] = this.getError(value);
            }
        }

        return errors;
    }

    static getError(errors: string[]) {
        return errors[0];
    }

    static required(value: string) {
        if(value.length) {
            return [];
        }

        return ["Поле обязательно"];
    }
}

