import {IUser} from "@entities/user/api/models";
import {API_META, IPaginatorResponse} from "@shared/api";
import {create} from "zustand";
import {UserService} from "@entities/user/api";

interface UserState {
    meta: API_META;
    user: IUser;

    getMe: () => Promise<void>;
    getAll: () => any;
}

const useUserStore = create<UserState>((set, get) => ({
    meta: API_META.INITIAL,
    user: {},

    getMe: async () => {
        if(get().meta === API_META.LOADING) {
            return;
        }

        set({meta: API_META.LOADING});

        try {
            const response = await UserService.getMe();
            console.log(response);
            set({user: response, meta: API_META.SUCCESS});
        }
        catch (error) {
            console.log("not get");
            set({meta: API_META.ERROR});
            throw error;
        }
    },

    getAll: async () => {
        if(get().meta === API_META.LOADING) {
            throw new Error();
        }

        set({meta: API_META.LOADING});

        try {
            const response = await UserService.getAll();
            set({meta: API_META.SUCCESS});
            return response;
        }
        catch (error) {
            set({meta: API_META.ERROR});
            throw new Error();
        }
    }
}));

export class UserStore {
    static useMeta = () => useUserStore(state => state.meta);
    static useUser = () => useUserStore(state => state.user);

    static getMe = useUserStore.getState().getMe;
    static getAll = useUserStore.getState().getAll;
}
