import {Validator} from "@shared/lib/validator/base";

export interface IRecoveryValidatorParams {
    email: string;
}

export interface IRecoveryValidatorErrors extends Partial<{
    email: string;
}> {}

export class RecoveryValidator {
    static validate(data: IRecoveryValidatorParams): IRecoveryValidatorErrors {
        return Validator.validate({
            email: Validator.required(data.email)
        });
    }
}
