import React, {useEffect} from 'react';
import {useTaskTable} from "@shared/hooks";
import {IPersonalTask, PERSONAL_TASK_QUERIES, PersonalTaskRepository} from "@entities/task";
import {ITaskStatus} from "@entities/status";
import {ITaskTag} from "@entities/tag";
import {LoaderHandler, TaskTable, TaskTableCell, TaskTableRow} from "@shared/ui";
import {ColumnDef, createColumnHelper, flexRender} from "@tanstack/react-table";
import {TaskTrait} from "@entities/trait";

const columnHelper = createColumnHelper<IPersonalTask<ITaskStatus, ITaskTag>>();

const tableColumns: ColumnDef<IPersonalTask<ITaskStatus, ITaskTag>, any>[] = [
    // {
    //     accessorKey: "name",
    //     header: "Название",
    // },
    columnHelper.accessor("name", {
        header: "Название",
    }),
    columnHelper.accessor("status", {
        header: "Статус",
        cell: (props) => props.getValue() && (
            <TaskTrait trait={props.getValue<ITaskStatus>()} />
        )
    }),
    columnHelper.accessor("tags", {
        header: "Теги",
        cell: (props) => props.getValue<ITaskTag[]>()?.map(tag => (
            <TaskTrait key={tag.id} trait={tag} />
        ))
    }),
    // {
    //     accessorKey: "tags",
    //     cell: (info) => (info.getValue() as ITaskTag[])?.map(tag => (
    //         <TaskTrait trait={tag} />
    //     ))
    //     ,
    //     header: "Теги"
    // },
    {
        accessorKey: "deadline",
        header: "Дедлайн"
    },
    {
        accessorKey: "description",
        header: "Описание"
    }
];

export const PersonalTaskTable = () => {
    const {
        table, tasks, updateTable
    } = useTaskTable<IPersonalTask<ITaskStatus, ITaskTag>>({
        tableColumns: tableColumns,
        queryKey: PERSONAL_TASK_QUERIES.TASKS,
        apiGet: () => PersonalTaskRepository.getList()
    });

    useEffect(() => {
        updateTable();
    }, []);

    return (
        <LoaderHandler isLoaded={!!tasks}>
            <TaskTable>
                <thead>
                {
                    table.getHeaderGroups().map(headerGroup => (
                        <TaskTableRow header key={headerGroup.id}>
                            {
                                headerGroup.headers.map(header => (
                                    <TaskTableCell header key={header.id}>
                                        {
                                            flexRender(header.column.columnDef.header, header.getContext())
                                        }
                                    </TaskTableCell>
                                ))
                            }
                        </TaskTableRow>
                    ))
                }
                </thead>
                <tbody>
                {
                    table.getRowModel().rows.map(row => (
                        <TaskTableRow key={row.id}>
                            {
                                row.getVisibleCells().map(cell => (
                                    <TaskTableCell key={cell.id}>{
                                        flexRender(cell.column.columnDef.cell, cell.getContext())
                                    }</TaskTableCell>
                                ))
                            }
                        </TaskTableRow>
                    ))
                }
                </tbody>
            </TaskTable>
        </LoaderHandler>
    );
};
