import {GetValidator} from "@shared/lib";

export class AuthValidator {
    static username: GetValidator<string, string> = (
        message = "Некорректный логин"
    ) => {
        const regex = /^[\w.@+-]+\Z/;
        return async (value) => (
            regex.test(value) ? message : null
        );
    }
}
