import {Validator} from "@shared/lib/validator/base";

export interface IRegistrationValidatorParams {
    username: string;
    firstName: string;
    lastName: string;
    email: string;
    password: string;
    confirmPassword: string;
}

export interface IRegistrationValidatorErrors extends Partial<{
    username: string;
    firstName: string;
    lastName: string;
    email: string;
    password: string;
    confirmPassword: string;
}> {}

export class RegistrationValidator {
    static validate(data: IRegistrationValidatorParams): IRegistrationValidatorErrors {
        return Validator.validate({
            username: this.username(data.username),
            firstName: this.firstName(data.firstName),
            lastName: this.lastName(data.lastName),
            email: this.email(data.email),
            password: Validator.required(data.password),
            confirmPassword: Validator.required(data.confirmPassword)
        });
    }

    static username(username: string): string[] {
        const errors: string[] = [];

        if(errors.push(...Validator.required(username))) {
            return errors;
        }

        const regex = /^[\w.@+-]+\Z/;

        if(!regex.test(username)) {
            errors.push("Некорректный логин");
        }

        return errors;
    }

    static firstName(firstName: string): string[] {
        const errors: string[] = [];

        if(errors.push(...Validator.required(firstName))) {
            return errors;
        }

        console.log(firstName);

        const regex = /^[A-Za-zА-Яа-я]+$/;

        if(!regex.test(firstName)) {
            errors.push("Некорректное имя");
        }

        return errors;
    }

    static lastName(lastName: string): string[] {
        const errors: string[] = [];

        if(errors.push(...Validator.required(lastName))) {
            return errors;
        }

        const regex = /^[A-Za-zА-Яа-я]+$/;

        if(!regex.test(lastName)) {
            errors.push("Некорректная фамилия");
        }

        return errors;
    }

    static email(email: string): string[] {
        const errors: string[] = [];

        if(errors.push(...Validator.required(email))) {
            return errors;
        }

        const regex = /^[\w\d]+@[\w]+\.[\w]+$/;

        if(!regex.test(email)) {
            errors.push("Некорректный адрес");
        }

        return errors;
    }
}
