import {CellContext, ColumnDef, createColumnHelper } from "@tanstack/react-table";
import {ITask, TaskName, UpdateTaskProps} from "@entities/task";
import {ITaskStatus} from "@entities/status";
import { TaskTrait } from "@entities/trait";
import React from "react";
import {ITaskTag} from "@entities/tag";
import {UseMutationResult, UseQueryResult} from "@tanstack/react-query";
import {IPaginatorResponse} from "@shared/api";

export function useTaskTableColumns<Task extends ITask, TaskUpdate extends ITask<number, number>>(props: {
    extendedColumns: ColumnDef<Task,  any>[];
    extendAfter: number;

    apiGetSubtasks: (task_id: number) => UseQueryResult<IPaginatorResponse<Task>>;
    apiUpdateTask: () => UseMutationResult<Task, Error, UpdateTaskProps<TaskUpdate>>;
}): ColumnDef<Task, any>[] {
    const {
        extendedColumns, extendAfter,
        apiGetSubtasks, apiUpdateTask
    } = props;

    const {
        mutate: updateMutation,
        isError: isUpdateError,
        error: updateError
    } = apiUpdateTask();

    const columnHelper = createColumnHelper<Task>();
    const columns: ColumnDef<Task,  any>[] = [
        columnHelper.accessor((task) => task, {
            header: "Название",
            cell: (context: CellContext<Task, Task>) => (
                <TaskName
                    onExpand={(e) => {

                    }}
                    onAddSubtask={(e) => {

                    }}
                    onChange={(e) => {
                        // console.log("was change", e.target.innerText);
                        updateMutation({
                            taskId: context.getValue().id,
                            task: {
                                name: e.target.innerText
                            } as Partial<TaskUpdate>
                        }, {
                            onSuccess: () => {
                                
                            }
                        });
                    }}
                    name={context.getValue().name}
                />
            )
        }),
        columnHelper.accessor(({status}) => status, {
            header: "Статус",
            cell: (context: CellContext<Task, ITaskStatus>) => context.getValue() && (
                <TaskTrait trait={context.getValue()} />
            )
        }),
        columnHelper.accessor(({tags}) => tags, {
            header: "Теги",
            cell: (context: CellContext<Task, ITaskTag[]>) => context.getValue()?.map(
                tag => (
                    <TaskTrait key={tag.id} trait={tag} />
                )
            )
        }),
        columnHelper.accessor(({deadline}) => deadline, {
            header: "Дедлайн",
        }),
        columnHelper.accessor(({description}) => description, {
            header: "Описание",
        }),
    ];

    switch(extendAfter) {
        case -1: {
            return [
                ...columns,
                ...extendedColumns
            ]
        }
        case 0: {
            return [
                ...extendedColumns,
                ...columns
            ]
        }
        default: {
            return [
                ...columns.slice(0, extendAfter),
                ...extendedColumns,
                ...columns.slice(extendAfter + 1)
            ];
        }
    }
}
