import React, {useEffect} from 'react';
import { useParams } from 'react-router';
import {useTaskTable} from "@shared/hooks";
import {SpaceTaskRepository} from "@entities/task";
import {QueryClient, useQueryClient} from "@tanstack/react-query";
import {LoaderHandler, TaskTable, TaskTableCellContent} from "@shared/ui";
import {ISpaceTask} from "@entities/task";
import {CellContext, createColumnHelper } from '@tanstack/react-table';
import {ITrait, TaskTrait, TaskTraitAdd} from "@entities/trait";
import {TagRepository} from "@entities/tag/repository";

export const SpaceTaskTable = () => {
    const space = parseInt(useParams().space!);

    const columnHelper = createColumnHelper<ISpaceTask>();

    const queryClient = useQueryClient();

    const {
        mutate: updateMutation,
        // isError: isUpdateError,
        // error: updateError
    } = SpaceTaskRepository.updateTask(space, queryClient);

    const {
        headers, rows,
        tasks, updateTable
    } = useTaskTable<ISpaceTask, ISpaceTask<number, number, number>>({
        extendAfter: 3,
        extendedColumns: [
            columnHelper.accessor(task => task, {
                header: "Ответственные",
                cell: (context: CellContext<ISpaceTask, ISpaceTask>) => (
                    <TaskTableCellContent hCenter vCenter>
                        {
                            context.getValue().responsible?.map(
                                resUser => (
                                    <TaskTrait
                                        removable
                                        key={resUser.id}
                                        trait={{
                                            id: resUser.id,
                                            name: resUser.username,
                                            color: resUser.color
                                        } as ITrait}
                                        onRemove={(responsibleId) => {
                                            updateMutation({
                                                taskId: context.getValue().id,
                                                task: {
                                                    responsible: context.getValue().responsible
                                                        .filter(resUser => resUser.id !== responsibleId)
                                                        .map(tag => tag.id)
                                                } as Partial<ISpaceTask<number, number, number>>
                                            });
                                        }}
                                    />
                                )
                            )
                        }
                        <TaskTraitAdd
                            list={context.getValue().staff.length ? context.getValue().staff : []}
                            onTraitSelect={(staffId) => {
                                if(context.getValue().responsible.map(resUser => resUser.id)) {
                                    return;
                                }

                                updateMutation({
                                    taskId: context.getValue().id,
                                    task: {
                                        responsible: [
                                            ...context.getValue().responsible.map(resUser => resUser.id),
                                            staffId
                                        ]
                                    } as Partial<ISpaceTask<number, number, number>>
                                });
                            }}
                        />
                    </TaskTableCellContent>
                )
            })
        ],
        apiGet: (params?: string) => SpaceTaskRepository.getList(space, params),
        apiUpdateTask: (queryClient: QueryClient) => SpaceTaskRepository.updateTask(space, queryClient),
    });

    useEffect(() => {
        updateTable();
    }, [updateTable]);

    return (
        <LoaderHandler isLoaded={!!tasks}>
            <TaskTable headers={headers} rows={rows} />
        </LoaderHandler>
    );
};
