import {IValidatorData, Validator} from "@shared/lib/validator/base";

interface ILoginValidatorParams {
    username: string;
    password: string;
}

export class LoginValidator {
    static validate(data: ILoginValidatorParams) {
        return Validator.validate({
            username: Validator.required(data.username),
            password: Validator.required(data.password)
        });
    }
}
