import { ValidatorData } from "./models";

export class Validator {

    static validate(data: ValidatorData): Partial<ValidatorData> {
        const errors: Partial<ValidatorData> = {};

        for(const key in data) {
            const error = this.getError(data[key] as string[]);
            if(error) {
                errors[key] = error;
            }
        }

        return errors;
    }

    private static getError(errors: string[]): string {
        return errors[0];
    }

    static hasError(errors: ValidatorData) {
        for(const key in errors) {
            if(errors[key]) {
                return true;
            }
        }

        return false;
    }

    protected static required(value: string) {
        const errors: string[] = [];

        if(!value.length && errors.push("Поле обязательно")) {
            return errors;
        }

        const regex = /(<(?:"[^"]*"['"]*|'[^']*'['"]*|[^'">])+>)/g;
        if(regex.test(value)) {
            errors.push("Обнаружены недопустимые символы");
        }

        return errors;
    }
}

