import {ILoginParams, IRegistrationParams, IRegistrationResponse} from "./models";
import {apiInstance} from "@shared/api";

const BASE_URL = "auth/";

const ENDPOINTS = {
    LOGIN: `${BASE_URL}login/`,
    REGISTRATION: `${BASE_URL}registration/`,
    LOGOUT: `${BASE_URL}logout/`,
    RECOVERY_PASSWORD: `${BASE_URL}recovery_password/`,
    RECOVERY_PASSWORD_CONFIRM: `${BASE_URL}recovery_password_confirm/`,
}

export class AuthService {
    static async login(params: ILoginParams): Promise<void> {
        return await apiInstance.post(ENDPOINTS.LOGIN, params);
    }

    static async registration(params: IRegistrationParams): Promise<IRegistrationResponse> {
        return await apiInstance.post(ENDPOINTS.REGISTRATION, params);
    }

    static async logout(): Promise<void> {
        return await apiInstance.post(ENDPOINTS.LOGOUT, {});
    }

    static async recoveryPassword(): Promise<void> {
        return await apiInstance.post(ENDPOINTS.LOGOUT, {});
    }
}
