import React, {useEffect} from 'react';
import { HeaderSkeleton } from './HeaderSkeleton';
import {userStore} from "@entities/user";
import cn from "classnames";

import s from "./styles.module.scss";
import {ASSETS_PATH, ROUTER_URL} from "@shared/config";
import {NavLink, useLocation} from "react-router-dom";
import {AuthRepository} from "@entities/session";

export const Header = () => {
    const profile = userStore.useProfile();
    const path = useLocation().pathname;

    useEffect(() => {
        if(!profile) {
            userStore.getMe();
        }
    }, [profile]);

    const onLogout = async () => await AuthRepository.logout();

    if(!profile) {
        return <HeaderSkeleton />
    }

    return (
        <div className={s.header}>
            <div className={s.headerProfile}>
                <p className={s.headerProfile__username} style={{color: profile!.color}}>{profile!.user.username}</p>

                <img
                    className={s.headerProfile__image}
                    src={ASSETS_PATH.ACCOUNT}
                    alt={""}
                />
            </div>

            <div className={s.header__line} />

            <NavLink className={cn(s.headerLink, {[s.headerLink_active]: path === ROUTER_URL.PROFILE})} to={ROUTER_URL.PROFILE}>
                <p className={s.headerLink__label}>Профиль</p>
                <img className={s.headerLink__image} src={ASSETS_PATH.HOME} alt={""} />
            </NavLink>

            <NavLink className={cn(s.headerLink, {[s.headerLink_active]: path === ROUTER_URL.PERSONAL})} to={ROUTER_URL.PERSONAL}>
                <p className={s.headerLink__label}>Мои задачи</p>
                <img className={s.headerLink__image} src={ASSETS_PATH.TASK} alt={""} />
            </NavLink>

            <NavLink className={cn(s.headerLink, {[s.headerLink_active]: path === ROUTER_URL.SPACES})} to={ROUTER_URL.SPACES}>
                <p className={s.headerLink__label}>Пространства</p>
                <img className={s.headerLink__image} src={ASSETS_PATH.SPACES} alt={""} />
            </NavLink>

            <div className={s.header__line} />

            <div className={s.header__line} />

            <NavLink className={cn(s.headerLink, {[s.headerLink_active]: path === ROUTER_URL.SETTINGS})} to={ROUTER_URL.SETTINGS}>
                <p className={s.headerLink__label}>Настройки</p>
                <img className={s.headerLink__image} src={ASSETS_PATH.SETTINGS} alt={""} />
            </NavLink>

            <NavLink className={s.headerLink} onClick={onLogout} to={""}>
                <p className={s.headerLink__label}>Выход</p>
                <img className={s.headerLink__image} src={ASSETS_PATH.LOGOUT} alt={""} />
            </NavLink>
        </div>
    );
};
