import React from 'react';
import {createBrowserRouter, LoaderFunction, Navigate, RouteObject} from "react-router-dom";
import {ROUTER_URL} from "@shared/config";
import {GuestGuard} from "@features/guards/guest_guard/ui/GuestGuard";
import {AuthGuard} from "@features/guards/auth_guard/ui/AuthGuard";
import {LoginPage, PersonalPage} from "@pages";
import {AuthStore} from "@entities/user";

const routerLoader = async () => {
    await AuthStore.check();
    return true;
}

const routes: RouteObject[] = [
    {
        index: true,
        element: <Navigate to={ROUTER_URL.PERSONAL} replace />
    },

    {
        path: ROUTER_URL.LOGIN,
        element: (
            <GuestGuard>
                <LoginPage />
            </GuestGuard>
        )
    },
    {
        path: ROUTER_URL.REGISTRATION,
        element: (
            <GuestGuard>
                <div>Регистрация</div>
            </GuestGuard>
        )
    },
    {
        path: ROUTER_URL.RECOVERY,
        element: (
            <GuestGuard>
                <div>Забыли пароль</div>
            </GuestGuard>
        )
    },

    {
        path: ROUTER_URL.PERSONAL,
        element: (
            <AuthGuard>
                <PersonalPage />
            </AuthGuard>
        )
    },
];

function attachLoader(routes: RouteObject[], loader: LoaderFunction<any>) {
    for (const route of routes) {
        if (route.path) {
            route.loader = loader;
        }
        if (route.children?.length) {
            attachLoader(route.children, loader);
        }
    }
    return routes;
}

export const Router = createBrowserRouter([
    {
        path: ROUTER_URL.ROOT,
        children: attachLoader(routes, routerLoader)
    },
    {
        path: ROUTER_URL.ERROR_404,
        element: (<div>Ошибка ебаный в рот</div>)
    },
    {
        path: "*",
        element: <Navigate to={ROUTER_URL.ERROR_404} replace />
    }
]);
