import {Validator} from "@shared/lib/validator/base";

export interface IRegistrationValidatorParams {
    username: string;
    firstName: string;
    lastName: string;
    email: string;
    password: string;
    confirmPassword: string;
}

export interface IRegistrationValidatorErrors extends Partial<{
    username: string;
    firstName: string;
    lastName: string;
    email: string;
    password: string;
    confirmPassword: string;
}> {}

export class RegistrationValidator {
    static validate(data: IRegistrationValidatorParams): IRegistrationValidatorErrors {
        return Validator.validate({
            username: this.username(data.username),
            password: Validator.required(data.password)
        });
    }

    static username(username: string): string[] {
        const errors: string[] = [];

        if(errors.push(...Validator.required(username))) {
            return errors;
        }

        const regex = /^[\w.@+-]+\Z/;

        if(!regex.test(username)) {
            errors.push("Некорректный логин");
        }

        return errors;
    }
}
