import { ColumnDef, createColumnHelper } from "@tanstack/react-table";
import {ITask} from "@entities/task";

export function useTaskTableColumns<Task extends ITask>(props: {
    extendedColumns: ColumnDef<Task,  any>[];
    extendAfter: number;
}): ColumnDef<Task, any>[] {
    const {extendedColumns, extendAfter} = props;

    const columnHelper = createColumnHelper<Task>();
    const columns: ColumnDef<Task,  any>[] = [
        columnHelper.accessor(({name}) => name, {
            header: "Название",
        }),
        columnHelper.accessor(({status}) => status, {
            header: "Статус",
        }),
        columnHelper.accessor(({tags}) => tags, {
            header: "Теги",
        }),
        columnHelper.accessor(({deadline}) => deadline, {
            header: "Дедлайн",
        }),
        columnHelper.accessor(({description}) => description, {
            header: "Описание",
        }),
    ];

    switch(extendAfter) {
        case -1: {
            return [
                ...columns,
                ...extendedColumns
            ]
        }
        case 0: {
            return [
                ...extendedColumns,
                ...columns
            ]
        }
        default: {
            return [
                ...columns.slice(0, extendAfter),
                ...extendedColumns,
                ...columns.slice(extendAfter + 1)
            ];
        }
    }
}
