import React, {ChangeEvent, useState} from 'react';
import {
    IRegistrationValidatorErrors,
    RegistrationValidator,
    Validator
} from "@shared/lib";
import {AuthStore} from "@entities/user";
import {Form, FormInput, FormReference, FormSubmit} from "@shared/ui";
import s from "@widgets/auth/login_form/ui/styles.module.scss";
import {ROUTER_URL} from "@shared/config";
import {IRegistrationValidatorParams} from "@shared/lib/validator/auth/registration";

export const RegistrationForm = () => {
    const [params, setParams] = useState<IRegistrationValidatorParams>({
        username: "",
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        confirmPassword: ""
    });
    const [errorHandler, setErrorHandler] = useState<IRegistrationValidatorErrors>({});

    const onSubmit = async () => {
        const errors = RegistrationValidator.validate(params);

        setErrorHandler(errors);

        if(Validator.hasError(errors)) {
            return;
        }

        try {

        }
        catch {

        }

    }

    const onChange = (e: ChangeEvent<HTMLInputElement>, key: keyof IRegistrationValidatorParams) => {
        if(errorHandler[key]) {
            setErrorHandler({...errorHandler, [key]: undefined});
        }
        const value = e.target.value;
        setParams({...params, [key]: value});
    }

    return (
        <Form className={s.loginForm}
              onSubmit={onSubmit}
        >
            <FormInput type={"text"} placeholder={"Введите логин..."} mask={""}
                       header={"Логин"}
                       onChange={(e) => onChange(e, "username")}
                       error={errorHandler.username}
            />
            <FormInput type={"text"} placeholder={"Введите имя..."} mask={""}
                       header={"Имя"}
                       onChange={(e) => onChange(e, "firstName")}
                       error={errorHandler.firstName}
            />
            <FormInput type={"text"} placeholder={"Введите фамилию..."} mask={""}
                       header={"Фамилия"}
                       onChange={(e) => onChange(e, "lastName")}
                       error={errorHandler.lastName}
            />
            <FormInput type={"text"} placeholder={"Введите почту..."} mask={""}
                       header={"Электронная почта"}
                       onChange={(e) => onChange(e, "email")}
                       error={errorHandler.email}
            />
            <FormInput type={"password"} placeholder={"Введите пароль..."} mask={""}
                       name={"password"}
                       header={"Пароль"}
                       onChange={(e) => onChange(e, "password")}
                       error={errorHandler.password}
            />
            <FormInput type={"password"} placeholder={"Повторите пароль..."} mask={""}
                       name={"password"}
                       header={"Подтвердждение пароля"}
                       onChange={(e) => onChange(e, "confirmPassword")}
                       error={errorHandler.confirmPassword}
            />

            <FormSubmit>Регистрация</FormSubmit>
            <FormReference to={ROUTER_URL.LOGIN}>Перейти к авторизации</FormReference>
        </Form>
    );
};
