import React, {FC, MouseEvent, useState} from 'react';
import {ITrait} from "@entities/trait";
import {ASSETS_PATH} from "@shared/config";

import s from "./styles.module.scss";

type TaskTraitAddProps = {
    list?: ITrait[];
};

export const TaskTraitAdd: FC<TaskTraitAddProps> = (props) => {
    const {list} = props;

    const [isMenu, setIsMenu] = useState(false);
    const [position, setPosition] = useState<{x: number, y: number}>({x: 0, y: 0});

    const onMenu = (e: MouseEvent<HTMLImageElement>) => {
        setPosition({x: e.clientX, y: e.clientY});

        setIsMenu(true);
    }

    return (
        <React.Fragment>
            <img
                className={s.taskTraitAdd}
                src={ASSETS_PATH.PLUS}
                title={"Добавить"}
                alt={""}
                onClick={onMenu}
            />
            {
                isMenu && (
                    <div
                        className={s.taskTraitList}
                        style={{left: position.x, top: position.y}}
                        onBlur={() => {
                            setIsMenu(false);
                            console.log("blur");
                        }}
                    >
                            {
                                list?.map(trait => (
                                    <div className={s.taskTraitList__button} key={trait.id}>{trait.name}</div>
                                ))
                            }
                    </div>
                )
            }
        </React.Fragment>
    );
};
