import React, {ChangeEvent, FC, FormEvent, useState} from 'react';
import {Form, FormInput, FormProps} from "@shared/ui";
import {Validator} from "@shared/lib";
import {VALIDATION_INPUT_PLACEHOLDER, VALIDATION_INPUT_HEADER} from "@widgets/validation_form/config/input";

type ValidationFormType = {
    /**
     * Валидатор формы
     */
    validator: Validator;

    /**
     * Конфиг
     */
    config?: ValidationFormConfig;
} & FormProps;

interface ValidationFormConfig {
}

export const ValidationForm: FC<ValidationFormType> = (props) => {
    const {children, validator, config, onSubmit, ...other} = props;

    const [keys] = useState<string[]>(Object.keys(validator.defaultData));
    const [params, setParams] = useState<typeof validator.defaultData>(validator.defaultData);
    const [errorHandler, setErrorHandler] = useState<Partial<typeof validator.defaultData>>({});

    const _onSubmit = (e: FormEvent<HTMLFormElement>) => {
        const errors = validator.validate(params);
        setErrorHandler(errors);
        if(validator.hasError(errors)) {
            return;
        }

        if(onSubmit) onSubmit(e);
    };

    const onChange = (e: ChangeEvent<HTMLInputElement>) => {
        const key = e.currentTarget.accessKey;
        if(errorHandler[key]) {
            setErrorHandler({...errorHandler, [key]: undefined});
        }
        const value = e.target.value;
        setParams({...params, [key]: value});
    };

    return (
        <Form onSubmit={_onSubmit} {...other}>
            {
                keys.map((value: string) => (
                    <FormInput key={value}
                               accessKey={value}
                               mask={""}
                               onChange={onChange}
                               header={VALIDATION_INPUT_HEADER[value] as string || ""}
                               placeholder={VALIDATION_INPUT_PLACEHOLDER[value] as string || "..."}
                    />
                ))
            }
            {children}
        </Form>
    );
};
