import React, {lazy} from 'react';
import {createBrowserRouter, LoaderFunction, Navigate, RouteObject, useRoutes} from "react-router-dom";
import {ROUTER_URL} from "@shared/config";
import {Loader} from "@shared/ui";
import {Spinner} from "@shared/ui/spinner";
import {AuthLoader} from "@features";
import {AuthGuard, GuestGuard, sessionStore} from "@entities/session";
import {UserGuard} from "@entities/session/ui/UserGuard";
import {userStore} from "@entities/user";

const LoginPage = Loader(lazy(() => import("@pages/login")));
const RegistrationPage = Loader(lazy(() => import("@pages/registration")));
const RecoveryPage = Loader(lazy(() => import("@pages/recovery")));

const PersonalPage = Loader(lazy(() => import("@pages/personal")));

const routerLoader = async () => {
    // console.log(AuthStore.useSession);
    // AuthStore.check();
    // sessionStore.setUser("test session");
    console.log(sessionStore.useSession());
    return true;
}

const routes: RouteObject[] = [

];

function attachLoader(routes: RouteObject[], loader: LoaderFunction<any>) {
    for (const route of routes) {
        if (route.path || route.index) {
            route.loader = loader;
        }
        if (route.children?.length) {
            attachLoader(route.children, loader);
        }
    }
    return routes;
}

export const Router = () => {
    const isAuth = sessionStore.useSession();
    const isUser = !!userStore.useUser();

    return useRoutes([
        {
            path: ROUTER_URL.ROOT,
            // element: (<AuthLoader />),
            children: [
                {
                    index: true,
                    element: (
                        <Navigate to={ROUTER_URL.PERSONAL} replace />
                    )
                },

                {
                    path: ROUTER_URL.LOGIN,
                    element: (
                        <GuestGuard isAuth={isAuth}>
                            <LoginPage />
                        </GuestGuard>
                    )
                },
                {
                    path: ROUTER_URL.REGISTRATION,
                    element: (
                        <GuestGuard isAuth={isAuth}>
                            <RegistrationPage />
                        </GuestGuard>
                    )
                },
                {
                    path: ROUTER_URL.RECOVERY,
                    children: [
                        {
                            index: true,
                            element: (
                                <GuestGuard isAuth={isAuth}>
                                    <RecoveryPage />
                                </GuestGuard>
                            )
                        },
                        {
                            path: ":uid/:token",
                            element: (
                                <GuestGuard isAuth={isAuth}>
                                    <RecoveryPage change />
                                </GuestGuard>
                            )
                        },
                        {
                            path: "*",
                            element: <Navigate to={ROUTER_URL.RECOVERY} replace />
                        }
                    ]

                },

                {
                    path: ROUTER_URL.PERSONAL,
                    element: (
                        <UserGuard isAuth={isAuth} isUser={isUser}>
                            <PersonalPage />
                        </UserGuard>
                    )
                },
                {
                    path: ROUTER_URL.PROFILE,
                    element: (
                        <UserGuard isAuth={isAuth} isUser={isUser}>
                            {/*<PersonalPage />*/}
                        </UserGuard>
                    )
                },
            ]
        },
        {
            path: ROUTER_URL.ERROR_404,
            element: (<div>Ошибка ебаный в рот</div>)
        },
        {
            path: "*",
            element: <Navigate to={ROUTER_URL.ERROR_404} replace />
        }
    ]);
}
