import {useEffect, useState} from "react";
import {IPaginatorResponse} from "@shared/api";

export function useTaskTable<Task>(props: {
    getCallback: () => {
        isLoading: boolean;
        isError: boolean;
        data: IPaginatorResponse<Task> | null;
    }
}): {
    tasks: Task[] | null;
} {
    const {getCallback} = props;

    const {isLoading, isError, data: response} = getCallback();

    return {
        tasks: response?.results || null,
    };
}
