import {DefaultField} from "@shared/types";
import {FormEventHandler, useState} from "react";

export function useForm<Field extends DefaultField, Response>(props: {
    fields: Field[];
    apiCall: () => Promise<Response>;
    onSuccess?: (response: Response) => void;
    onFailure?: (error: string) => void;
}): {
    isSending: boolean;
    sendingError: string;
    hasFieldErrors: boolean;
    handleFormSubmit: FormEventHandler<HTMLFormElement>;
} {
    const {fields, apiCall, onSuccess, onFailure} = props;

    const [isSending, setIsSending] = useState(false);
    const [sendingError, setSendingError] = useState("");

    const handleFormSubmit: FormEventHandler<HTMLFormElement> = async (event) => {
        event.preventDefault();

        const errors = await Promise.all(fields.map(field => field.hasError()));
        const isFormValid = errors.every(error => !error);

        if(!isFormValid) return;

        setIsSending(true);
        setSendingError("");

        try {
            const response = await apiCall();
            onSuccess?.(response);
        }
        catch (error) {
            const message = (
                error instanceof Error ?
                    error.message :
                    "Произошла ошибка при выполнении запроса"
            );

            setSendingError(message);
            onFailure?.(message);
        }
        finally {
            setIsSending(false);
        }
    };

    const hasFieldErrors = fields.some(field => !!field.error);

    return {
        isSending,
        sendingError,
        hasFieldErrors,
        handleFormSubmit
    };
};
