import {apiInstance, IPaginatorResponse} from "@shared/api";
import {IPersonalTask} from "@entities/task";
import {ITaskStatus} from "@entities/status";
import { ITaskTag } from "@entities/tag";

const BASE_URL = "task_me/";

const ENDPOINTS = {
    ROOT: `${BASE_URL}`,
    SPACE_TASKS: `${BASE_URL}spaces/`,
}

export class PersonalTaskService {
    static async getList(): Promise<IPaginatorResponse<IPersonalTask<ITaskStatus, ITaskTag>>> {
        return await apiInstance.get(ENDPOINTS.ROOT);
    }

    static async create(task: Partial<IPersonalTask<number, number>>):
        Promise<IPaginatorResponse<IPersonalTask<ITaskStatus, ITaskTag>>> {
        return await apiInstance.post(ENDPOINTS.ROOT, task);
    }

    static async getSpaceTasks(): Promise<IPaginatorResponse<IPersonalTask<ITaskStatus, ITaskTag>>> {
        return await apiInstance.get(ENDPOINTS.SPACE_TASKS);
    }

    static async getTask(task_id: number): Promise<IPersonalTask<ITaskStatus, ITaskTag>> {
        return await apiInstance.get(`${ENDPOINTS.ROOT}${task_id}/`);
    }

    static async updateTask(task_id: number, task: Partial<IPersonalTask<number, number>>): Promise<IPersonalTask<ITaskStatus, ITaskTag>> {
        return await apiInstance.patch(`${ENDPOINTS.ROOT}${task_id}/`, task);
    }

    static async removeTask(task_id: number): Promise<void> {
        return await apiInstance.delete(`${ENDPOINTS.ROOT}${task_id}/`);
    }

    static async getSubtasks(task_id: number): Promise<IPaginatorResponse<IPersonalTask<ITaskStatus, ITaskTag>>> {
        return await apiInstance.get(`${ENDPOINTS.ROOT}${task_id}/subtasks/`);
    }

    static async createSubtask(task_id: number, task: Partial<IPersonalTask<number, number>>): Promise<IPaginatorResponse<IPersonalTask<ITaskStatus, ITaskTag>>> {
        return await apiInstance.post(`${ENDPOINTS.ROOT}${task_id}/subtasks/`, task);
    }
}
