import {Validator} from "../base";
import {ILoginParams} from "@entities/user";

export class LoginValidator {
    static validate(data: ILoginParams): Partial<ILoginParams> {
        return Validator.validate({
            username: Validator.required(data.username),
            password: Validator.required(data.password)
        });
    }
}
