export function getDateFromDeadline(timestamp: number) {
    const date = new Date(timestamp);

    return date.toLocaleDateString();
}

export function getInputDateFromDeadline(timestamp: number) {
    const date = new Date(timestamp);

    return date.toISOString();
}
