import React, {useCallback} from "react";
import {useQueryClient, UseQueryResult} from "@tanstack/react-query";
import {IPaginatorResponse} from "@shared/api";
import {ColumnDef, useReactTable, getCoreRowModel, Table, flexRender} from "@tanstack/react-table";

export function useTaskTable<Task extends object>(props: {
    tableColumns: ColumnDef<Task>[];
    queryKey: string;
    apiGet: () => UseQueryResult<IPaginatorResponse<Task>>;
}): {
    table: Table<Task>;
    headers: (JSX.Element | React.ReactNode)[];
    rows: (JSX.Element | React.ReactNode)[][];
    tasks: Task[] | null;
    updateTable: () => void;
} {
    const {tableColumns, queryKey, apiGet} = props;

    const queryClient = useQueryClient();

    const {
        data: response,
    } = apiGet();
    const tasks = response?.results || null;

    const updateTable = useCallback(async () => {
        await queryClient.resetQueries({
            queryKey: [queryKey]
        });
    }, [tasks]);

    const table = useReactTable({
        columns: tableColumns,
        data: tasks || [],
        getCoreRowModel: getCoreRowModel(),
        enableColumnResizing: true,
        columnResizeMode: "onChange"
    });

    const headers = table.getHeaderGroups()[0].headers.map(header =>
        flexRender(header.column.columnDef.header, header.getContext())
    );
    const rows = table.getRowModel().rows.map(row =>
        row.getVisibleCells().map(cell =>
            flexRender(cell.column.columnDef.cell, cell.getContext())
        )
    );

    return {
        table,
        headers,
        rows,
        tasks,
        updateTable
    }
}
