import React, {useEffect} from 'react';
import { useParams } from 'react-router';
import {useTaskTable} from "@shared/hooks";
import {SpaceTaskRepository} from "@entities/task";
import {QueryClient, useQueryClient} from "@tanstack/react-query";
import {LoaderHandler, TaskTable, TaskTableCellContent} from "@shared/ui";
import {ISpaceTask} from "@entities/task";
import {CellContext, createColumnHelper } from '@tanstack/react-table';
import {ITrait, TaskTrait, TaskTraitAdd} from "@entities/trait";
import {SpaceRepository} from "@entities/space";
import {ROUTES} from "@shared/config";
import { Navigate } from 'react-router-dom';

export const SpaceTaskTable = () => {
    const spaceParam = useParams().spaceId;
    const spaceId = spaceParam ? parseInt(spaceParam) : null;

    const columnHelper = createColumnHelper<ISpaceTask>();

    const queryClient = useQueryClient();

    const {
        data: space,
        isSuccess: isSpace
    } = SpaceRepository.getById(spaceId!)

    const {
        mutate: updateMutation,
        // isError: isUpdateError,
        // error: updateError
    } = SpaceTaskRepository.updateTask(spaceId!, queryClient);

    const {
        headers, rows,
        tasks, updateTable
    } = useTaskTable<ISpaceTask, ISpaceTask<number, number, number>>({
        extendAfter: 3,
        extendedColumns: [
            columnHelper.accessor(task => task, {
                header: "Ответственные",
                cell: (context: CellContext<ISpaceTask, ISpaceTask>) => (
                    <TaskTableCellContent hCenter vCenter>
                        {
                            context.getValue().responsibles?.map(
                                resUser => (
                                    <TaskTrait
                                        removable
                                        key={resUser.id}
                                        trait={{
                                            id: resUser.id,
                                            name: resUser.username,
                                            color: resUser.color
                                        } as ITrait}
                                        onRemove={(responsibleId) => {
                                            updateMutation({
                                                taskId: context.getValue().id,
                                                task: {
                                                    responsibles: context.getValue().responsibles
                                                        .filter(resUser => resUser.id !== responsibleId)
                                                        .map(tag => tag.id)
                                                } as Partial<ISpaceTask<number, number, number>>
                                            });
                                        }}
                                    />
                                )
                            )
                        }
                        <TaskTraitAdd
                            list={
                                space?.staff.map(user => {
                                    return {
                                        id: user.id,
                                        name: user.username,
                                        color: user.color
                                    }
                                }) as ITrait[]
                            }
                            onTraitSelect={(staffId) => {
                                if(context.getValue().responsibles.map(user => user.id).includes(staffId)) {
                                    return;
                                }

                                updateMutation({
                                    taskId: context.getValue().id,
                                    task: {
                                        responsibles: [
                                            ...context.getValue().responsibles.map(user => user.id),
                                            staffId
                                        ]
                                    } as Partial<ISpaceTask<number, number, number>>
                                });
                            }}
                        />
                    </TaskTableCellContent>
                )
            })
        ],
        apiGet: (params?: string) => SpaceTaskRepository.getList(spaceId!, params),
        apiUpdateTask: (queryClient: QueryClient) => SpaceTaskRepository.updateTask(spaceId!, queryClient),
    });

    useEffect(() => {
        updateTable();
    }, [updateTable]);

    if(!spaceId) {
        return <Navigate to={ROUTES.USER.SPACE.ROOT()} replace />
    }

    return (
        <LoaderHandler isLoaded={!!tasks && isSpace}>
            <TaskTable headers={headers} rows={rows} />
        </LoaderHandler>
    );
};
