import React, {ChangeEvent, useState} from 'react';
import {Form, FormInput, FormReference, FormSubmit} from "@shared/ui";
import {ROUTER_URL} from "@shared/config";

import s from "./styles.module.scss";
import {AuthStore} from "@entities/user";
import {ILoginValidatorErrors, ILoginValidatorParams, LoginValidator, Validator} from "@shared/lib";

export const LoginForm = () => {
    const [params, setParams] = useState<ILoginValidatorParams>({
        username: "",
        password: ""
    });
    const [errorHandler, setErrorHandler] = useState<ILoginValidatorErrors>({});

    const onSubmit = async () => {
        const errors = LoginValidator.validate(params);

        setErrorHandler(errors);

        if(Validator.hasError(errors)) {
            return;
        }

        try {
            await AuthStore.login({username: username, password: password});
        }
        catch {
            setErrorHandler({
                ...errorHandler,
                password: "Неверный логин или пароль!"
            });
        }
    }

    const onChangeGeneric = (e: ChangeEvent<HTMLInputElement>) => {
        
    }

    const onChangeUsername = (e: ChangeEvent<HTMLInputElement>) => {
        if(errorHandler.username) {
            setErrorHandler({...errorHandler, username: undefined});
        }
        const value = e.target.value;
        setParams({...params, username: value});
    }

    const onChangePassword = (e: ChangeEvent<HTMLInputElement>) => {
        if(errorHandler.password) {
            setErrorHandler({...errorHandler, password: undefined});
        }
        const value = e.target.value;
        setParams({...params, password: value});
    }

    return (
        <Form className={s.loginForm}
              onSubmit={onSubmit}
        >
            <FormInput type={"text"} placeholder={"Введите логин..."} mask={""}
                       header={"Логин"}
                       onChange={onChangeUsername}
                       error={errorHandler.username}
            />
            <FormInput type={"password"} placeholder={"Введите пароль..."} mask={""}
                       name={"password"}
                       header={"Пароль"}
                       onChange={onChangePassword}
                       error={errorHandler.password}
            />

            <FormReference to={ROUTER_URL.RECOVERY}>Забыли пароль?</FormReference>

            <FormSubmit>Войти</FormSubmit>
            <FormReference to={ROUTER_URL.REGISTRATION}>Перейти к регистрации</FormReference>
        </Form>
    );
};
