import {Validator} from "@shared/lib/validator/base";

export interface IRegistrationValidatorParams {
    username: string;
    firstName: string;
    lastName: string;
    email: string;
    password: string;
    confirmPassword: string;
}

export interface IRegistrationValidatorErrors extends Partial<{
    username: string;
    firstName: string;
    lastName: string;
    email: string;
    password: string;
    confirmPassword: string;
}> {}

export class RegistrationValidator {
    static validate(data: IRegistrationValidatorParams): IRegistrationValidatorErrors {
        return Validator.validate({
            username: Validator.required(data.username),
            password: Validator.required(data.password)
        });
    }
}
