import {IPersonalTask, PersonalTaskService} from "@entities/task";
import { useQuery } from "@tanstack/react-query";
import {IPaginatorResponse} from "@shared/api";
import {ITaskStatus} from "@entities/status";
import {ITaskTag} from "@entities/tag";

export const PERSONAL_TASK_QUERIES = {
    TASKS: "personalTasks",
    SUBTASKS: "personalSubtasks",
}

export class PersonalTaskRepository {
    static getList = (params?: string) => useQuery <
        IPaginatorResponse<IPersonalTask>,
        Error,
        IPaginatorResponse<IPersonalTask>,
        string[]
    >({
        queryKey: [PERSONAL_TASK_QUERIES.TASKS],
        queryFn: async () => {
            const response = await PersonalTaskService.getList(params);

            await new Promise(resolve => setTimeout(resolve, 500));

            return response;
        },
    });

    static updateTask = async (task_id: number, task: Partial<IPersonalTask<number, number>>) => {
        try {
            await PersonalTaskService.updateTask(task_id, task);
        }
        catch (error) {

        }
    }

    static getSubtasks = (task_id: number) => useQuery <
        IPaginatorResponse<IPersonalTask>,
        Error,
        IPaginatorResponse<IPersonalTask>,
        string[]
        >({
        queryKey: [PERSONAL_TASK_QUERIES.SUBTASKS],
        queryFn: async () => {
            const response = await PersonalTaskService.getSubtasks(task_id);

            return response;
        },
    });
}
