import React, {useEffect} from 'react';
import { HeaderSkeleton } from './HeaderSkeleton';
import {userStore} from "@entities/user";
import cn from "classnames";

import s from "./styles.module.scss";
import {ASSETS_PATH, ROUTER_URL} from "@shared/config";
import {NavLink} from "react-router-dom";

export const Header = () => {
    const user = userStore.useUser();

    useEffect(() => {
       userStore.getMe();
    }, []);

    if(!user) {
        return <HeaderSkeleton />
    }

    return (
        <div className={s.header}>
            <div className={s.headerProfile}>
                <p className={s.headerProfile__username}>{user.username}</p>

                <img
                    className={s.headerProfile__image}
                    src={ASSETS_PATH.ACCOUNT}
                    alt={""}
                />
            </div>

            <div className={s.header__line} />

            <NavLink className={s.headerLink} to={ROUTER_URL.PROFILE}>
                <p className={s.headerLink__label}>Профиль</p>
                <img className={s.headerLink__image} src={ASSETS_PATH.HOME} alt={""} />
            </NavLink>
        </div>
    );
};
