import React, {ChangeEvent, FC, useEffect, useState} from 'react';
import {Form, FormInput, FormProps} from "@shared/ui";
import {Validator} from "@shared/lib";

type ValidationFormType = {
    validator: Validator;

    config: Partial<ValidationFormConfig>;
} & FormProps;

interface ValidationFormConfig {

}

export const ValidationForm: FC<ValidationFormType> = (props) => {
    const {validator, onSubmit, ...other} = props;

    const [keys] = useState<string[]>(Object.keys(validator.defaultData));
    const [params, setParams] = useState<typeof validator.defaultData>(validator.defaultData);
    const [errorHandler, setErrorHandler] = useState<Partial<typeof validator.defaultData>>({});

    const _onSubmit = () => {
        const errors = validator.validate(params);
        setErrorHandler(errors);
