        }
    };

    const onChange = (e: ChangeEvent<HTMLInputElement>) => {
        const key = e.currentTarget.accessKey;
        if(errorHandler[key]) {
            setErrorHandler({...errorHandler, [key]: undefined});
        }
        const value = e.target.value;
        setParams({...params, [key]: value});
    };

    return (
        <Form onSubmit={_onSubmit} {...other}>
            {
                keys.map((value: string) => (
                    <FormInput key={value} accessKey={value} mask={""} onChange={onChange} />
                ))
            }
        </Form>
    );
};
