import {useEffect, useState} from "react";
import {IPaginatorResponse} from "@shared/api";

export function useTaskTable<Task>(props: {
    getCallback: () => {
        isLoading: boolean;
        isError: boolean;
        data: IPaginatorResponse<Task> | undefined;
    }
}): {
    tasks: Task[] | null;
} {
    const {getCallback} = props;

    const [tasks, setTasks] = useState<Task[] | null>(null);

    useEffect(() => {
        if(!tasks) {
            const {isLoading, isError, data} = getCallback();

            if(!isLoading || isError) return;

            setTasks(data!.results);
        }
    }, [tasks]);

    return {
        tasks,
    };
}
