import React from 'react';
import {Form, FormError, FormInput, FormReference, FormSubmit} from "@shared/ui";
import {ROUTER_URL} from "@shared/config";
import {useForm, useFormTextField} from "@shared/hooks";
import {CommonValidator} from "@shared/lib";
import {TextField} from "@shared/types";

export const RegistrationForm = () => {
    const username = useFormTextField("username", [CommonValidator.required()]);
    const firstName = useFormTextField("firstName", [CommonValidator.required()]);
    const lastName = useFormTextField("lastName", [CommonValidator.required()]);
    const email = useFormTextField("email", [CommonValidator.required()]);
    const password = useFormTextField("password", [CommonValidator.required()]);
    const confirmPassword = useFormTextField("confirmPassword", [CommonValidator.required()]);

    const form = useForm<TextField, void>({
        fields: [username, firstName, lastName, email, password, confirmPassword],
        apiCall: async () => {},
    });

    return (
        <Form onSubmit={form.handleFormSubmit} center wide>
            <FormInput type={"text"} placeholder={"Введите имя..."} mask={""}
                       header={"Имя"}
                       onChange={firstName.handleChange}
                       error={firstName.error}
            />
            <FormInput type={"text"} placeholder={"Введите логин..."} mask={""}
                       header={"Логин"}
                       onChange={username.handleChange}
                       error={username.error}
            />
            <FormInput type={"text"} placeholder={"Введите фамилию..."} mask={""}
                       header={"Фамилия"}
                       onChange={lastName.handleChange}
                       error={lastName.error}
            />
            <FormInput type={"password"} placeholder={"Введите пароль..."} mask={""}
                       header={"Пароль"}
                       onChange={password.handleChange}
                       error={password.error}
            />
            <FormInput type={"text"} placeholder={"Введите почту..."} mask={""}
                       header={"Электронная почта"}
                       onChange={email.handleChange}
                       error={email.error}
            />
            <FormInput type={"password"} placeholder={"Повторите пароль..."} mask={""}
                       header={"Подтвердите пароль"}
                       onChange={confirmPassword.handleChange}
                       error={confirmPassword.error}
            />

            <FormError>{form.sendingError}</FormError>
            <FormSubmit>Регистрация</FormSubmit>
            <FormReference to={ROUTER_URL.LOGIN}>Перейти к авторизации</FormReference>
        </Form>
    );
};
