import {useCallback, useEffect, useState} from "react";
import {useQueryClient, UseQueryResult} from "@tanstack/react-query";
import {IPaginatorResponse} from "@shared/api";
import {PERSONAL_TASK_QUERIES} from "@entities/task";

export function useTaskTable<Task>(props: {
    apiGet: () => UseQueryResult<IPaginatorResponse<Task>>;
}): {
    tasks: Task[] | null,
    updateTable: () => void;
} {
    const {apiGet} = props;

    const queryClient = useQueryClient();

    const {
        isLoading,
        isError,
        data: response
    } = apiGet();

    const updateTable = useCallback(() => {
        queryClient.invalidateQueries(PERSONAL_TASK_QUERIES.TASKS)
    }, []);

    return {
        tasks,
        updateTable
    }
}
