import React, {FC, InputHTMLAttributes, PropsWithChildren, useState} from 'react';

type TaskNameProps = PropsWithChildren<{
    name: string;
}> & InputHTMLAttributes<HTMLInputElement>;

export const TaskName: FC<TaskNameProps> = (props) => {
    const {name} = props;

    const [isEditing, setIsEditing] = useState(false);

    return (
        <div>
            <img
                
                title={"Раскрыть"}
                alt={""}
            />
            <div contentEditable={true}>{name}</div>
        </div>
    );
};
