import React from 'react';
import {Form, FormError, FormInput, FormReference, FormSubmit} from "@shared/ui";
import {ROUTER_URL} from "@shared/config";
import {useForm, useFormTextField} from "@shared/hooks";
import {CommonValidator} from "@shared/lib";
import {TextField} from "@shared/types";
import {useSearchParams} from "react-router-dom";

export const RecoveryPasswordForm = () => {
    const {uid, token} = useSearchParams()

    const email = useFormTextField("email", [CommonValidator.required()]);

    const form = useForm<TextField, void>({
        fields: [email],
        apiCall: async () => {},
    });

    return (
        <Form onSubmit={form.handleFormSubmit} center>
            <FormInput type={"text"} placeholder={"Введите пароль..."} mask={""}
                       header={"Пароль"}
                       onChange={email.handleChange}
                       error={email.error}
            />

            <FormError>{form.sendingError}</FormError>
            <FormSubmit>Восстановить</FormSubmit>
            <FormReference to={ROUTER_URL.LOGIN}>Вернуться к авторизации</FormReference>
        </Form>
    );
};
