import {useCallback, useEffect, useState} from "react";
import {useQueryClient, UseQueryResult} from "@tanstack/react-query";
import {IPaginatorResponse} from "@shared/api";
import {PERSONAL_TASK_QUERIES} from "@entities/task";
import {Column, useTable} from "react-table";

export function useTaskTable<Task>(props: {
    tableColumns: Column<Task>[],
    queryKey: string;
    apiGet: () => UseQueryResult<IPaginatorResponse<Task>>;
}): {
    tasks: Task[] | null,
    updateTable: () => void;
} {
    const {queryKey, apiGet} = props;

    const table = useTable()

    const queryClient = useQueryClient();

    const {
        data: response,
    } = apiGet();
    const tasks = response?.results || null;

    const updateTable = useCallback(async () => {
        await queryClient.resetQueries({
            queryKey: [queryKey]
        });
    }, []);

    return {
        tasks,
        updateTable
    }
}
