import {create} from "zustand";
import {API_META} from "@shared/api";
import {AuthService, ILoginParams, UserService, UserStore} from "@entities/user";
import Cookies from "js-cookie";
import {useQuery, UseQueryResult} from "@tanstack/react-query";
import {persist} from "zustand/middleware";

interface AuthState {
    meta: API_META;
    isAuth: boolean;

    session: string;

    login: (params: ILoginParams) => Promise<void>;
    logout: () => Promise<void>;
    check: () => Promise<void>;

    useTest: () => UseQueryResult;
}

const useAuthStore = create<AuthState>()(
    persist(
        (set, get) => ({
            meta: API_META.INITIAL,
            isAuth: false,

            session: "",

            login: async (params) => {
                if(get().meta === API_META.LOADING) {
                    return;
                }

                set({meta: API_META.LOADING});

                try {
                    await AuthService.login(params);
                    console.log(Cookies.get("sessionid"));
                    set({isAuth: true, meta: API_META.SUCCESS});
                }
                catch (error) {
                    set({meta: API_META.ERROR});
                    throw new Error("Неверный логин или пароль");
                }
            },

            logout: async () => {
                if(get().meta === API_META.LOADING) {
                    return;
                }

                set({meta: API_META.LOADING});

                try {
                    await AuthService.logout();
                    set({isAuth: false, meta: API_META.SUCCESS});
                }
                catch (error) {
                    set({meta: API_META.ERROR});
                }
            },

            check: async () => {
                if(get().meta === API_META.LOADING) {
                    return;
                }

                set({meta: API_META.LOADING});

                if(!Cookies.get("sessionid")) {
                    set({isAuth: false, meta: API_META.ERROR});
                    return;
                }

                try {
                    await UserStore.getMe();
                    set({isAuth: true, meta: API_META.SUCCESS});
                }
                catch {
                    set({isAuth: false, meta: API_META.ERROR});
                }
            },

            useTest: () => useQuery({
                queryKey: [],

                queryFn: async () => {
                    console.log("call query");
                    const response = await UserService.getMe();

                    return response;
                }
            })
}), {
    name: "session-storage",
}));

export class AuthStore {
    static test = () => useAuthStore.getState().useTest;
    static useMeta = () => useAuthStore(state => state.meta);
    static useAuth = () => useAuthStore(state => state.isAuth);

    static login = useAuthStore.getState().login;
    static logout = useAuthStore.getState().logout;
    static check = useAuthStore.getState().check;
}
