import React, {ChangeEvent, useState} from 'react';
import {Form, FormInput, FormReference, FormSubmit} from "@shared/ui";
import {ROUTER_URL} from "@shared/config";
import {AuthStore, ILoginParams} from "@entities/user";
import {useFormTextField} from "@shared/hooks";
import {CommonValidator} from "@shared/lib";
import {useForm} from "@shared/hooks/form/use-form";
import {TextField} from "@shared/types";

export const LoginForm = () => {
    const username = useFormTextField("username", [CommonValidator.required()]);
    const password = useFormTextField("password", [CommonValidator.required()]);

    const form = useForm<TextField, void>({
        fields: [username, password],
        apiCall: async () => await AuthStore.login({
            username: username.value,
            password: password.value
        }),
        onFailure: () => {
            console.log("ошибка запроса");
        }
    });
};

